package week1.assigment2;

public class NegativeToPositive {

	public static void main(String[] args) {
		// TODO Auto-generated method stub
		int num = -20;
		int Positive;
		if (num < 0) {
			Positive = num*-1;
			System.out.println("The number" +num+" is converted into" +Positive);
			
			
		}

	}

}
